import React, { useState, useEffect, useRef } from "react";
import { useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { IStore } from "../../interfaces/IStore";
import { CodeBlockContainer } from "../../styledComponents/codeBlockPageStyledComponents";
import hljs from 'highlight.js/lib/core';
import javascript from 'highlight.js/lib/languages/javascript';
// import 'highlight.js/styles/default.css';
import "highlight.js/styles/github.css";

export const CodeBlockPage: React.FC = () => {

    const { id = '' } = useParams();
    const codeBlocksData = useSelector((state: IStore) => state.codeBlocks.value);
    const currentCodeBlock = codeBlocksData.filter((codeBlock) => codeBlock._id === id);
    const [codeBlockCode, setCodeBlockCode] = useState(currentCodeBlock[0].code);
    const [codeBlockName, setCodeBlockName] = useState(currentCodeBlock[0].name);
    const [isMentor, setIsMentor] = useState(false);

    const codeRef = useRef<HTMLDivElement>(null);
    hljs.registerLanguage('javascript', javascript);
    useEffect(() => {
        if (codeRef.current) {
            hljs.highlightElement(codeRef.current);
            // hljs.highlightElement(codeRef.current);
        }
    }, [codeBlockCode]);

    const handleCodeChange = (event: React.FormEvent<HTMLDivElement>) => {
        if (event.currentTarget.textContent) {
            setCodeBlockCode(event.currentTarget.textContent);
        }
    };

    return (
        <CodeBlockContainer>
            <tex
                contentEditable={!isMentor}
                style={{ fontSize: "14px" }}
                ref={codeRef}
                className={`language-javascript`}
                onInput={handleCodeChange}
                dangerouslySetInnerHTML={{ __html: codeBlockCode }}
            />
        </CodeBlockContainer>
    )
}

export default CodeBlockPage;
